// styles.js

export const styles = {
  container: {
    flex: 1,
    padding: 20,
    width: '50%', // Take up 50% of the width
    aspectRatio: 1, // Maintain aspect ratio
  },
  labelContainer: {
    marginBottom: 20,
  },
  label: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  inputContainer: {
    flexDirection: 'row',
    marginBottom: 10,
  },
  input: {
    flex: 1, // Take up remaining space
    height: 100,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 8,
    padding: 10,
    marginRight: 10,
  },
  input_name: {
    flex: 1, // Take up remaining space
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 8,
    padding: 10,
    marginRight: 10,
  },
  chipContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginBottom: 20,
  },
  chip: {
    margin: 4,
  },
  button: {
    backgroundColor: '#FD8D14',
    padding: 10,
    borderRadius: 8,
    marginBottom: 10,
  },
  buttonText: {
    color: 'white',
    textAlign: 'center',
    fontWeight: 'bold',
  },
  containerImages: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  imageContainer: {
    width: '50%', // Take up 50% of the width
    aspectRatio: 1, // Maintain aspect ratio
    borderRadius: 8,
    overflow: 'hidden',
    marginRight: 10,
  },
  image: {
    flex: 1,
    resizeMode: 'cover',
  },
  errorText: {
    color: 'red',
    marginBottom: 10,
  },
  button_add: {
    backgroundColor: '#FD8D14',
    padding: 10,
    borderRadius: 8,
    marginBottom: 10,
  },
  buttonText_Add: {
    color: 'white',
    textAlign: 'center',
    fontWeight: 'bold',
  },
  button_cancel: {
    backgroundColor: 'gray',
    padding: 10,
    borderRadius: 8,
  },
  buttonText_Cancel: {
    color: 'white',
    textAlign: 'center',
    fontWeight: 'bold',
  },
};
