const styles = {
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#fff',
  },
  labelContainer: {
    marginBottom: 10,
  },
  label: {
    fontSize: 18,
  },
  input_name: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    paddingHorizontal: 5,
  },
  input: {
    height: 100,
    borderColor: 'gray',
    borderWidth: 1,
    paddingHorizontal: 5,
  },
  chipContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginBottom: 10,
  },
  chip: {
    marginHorizontal: 4,
    marginVertical: 2,
  },
  buttonText: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',
    backgroundColor: '#dcdcdc',
    textAlign: 'center', // Center the text horizontally
    textAlignVertical: 'center', // Center the text vertically (for Android)
  },
  buttonText_Add: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    backgroundColor: '#8b0000',
    textAlign: 'center', // Center the text horizontally
    textAlignVertical: 'center', // Center the text vertically (for Android)
  },
  buttonText_Cancel: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',
    backgroundColor: '#dcdcdc',
    textAlign: 'center', // Center the text horizontally
    textAlignVertical: 'center', // Center the text vertically (for Android)
  },
  header: {
    fontSize: 20,
    marginBottom: 16,
  },
  button: {
    padding: 10,
    borderRadius: 8,
    marginBottom: 16,
    shadowColor: '#000000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 4,
    elevation: 5,
  },
  button_add: {
    ...button,
    backgroundColor: '#8b0000',
  },
  button_cancel: {
    ...button,
    backgroundColor: '#dcdcdc',
  },
  imageContainer: {
    ...Platform.select({
      android: {
        elevation: 5,
      },
      ios: {
        // iOS-specific styles if needed
      },
    }),
  },
  image: {
    width: 150,
    height: 150,
    borderRadius: 8,
  },
  containerImages: {
    flex: 1,
    justifyContent: 'flex-start', // Adjust this based on your layout needs
    alignItems: 'center',
  },
  errorText: {
    color: 'red',
    marginTop: 16,
  },
};

export default styles;
