const styles = {
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#fff',
  },

  stepTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 16,
  },

  labelContainer: {
    marginBottom: 16,
  },

  label: {
    fontSize: 16,
    marginBottom: 8,
  },

  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    padding: 8,
    fontSize: 16,
  },

  button: {
    backgroundColor: '#FD8D14',
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
    marginVertical: 16,
  },

  buttonText: {
    color: '#fff',
    fontSize: 16,
  },

  button_cancel: {
    backgroundColor: '#ccc',
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
    marginVertical: 16,
  },

  buttonText_Cancel: {
    color: '#fff',
    fontSize: 16,
  },

  button_next: {
    backgroundColor: '#4CAF50',
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
    marginVertical: 16,
  },

  buttonText_Next: {
    color: '#fff',
    fontSize: 16,
  },

  button_prev: {
    backgroundColor: '#2196F3',
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
    marginVertical: 16,
  },

  buttonText_Prev: {
    color: '#fff',
    fontSize: 16,
  },

  chipContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginBottom: 10,
  },

  chip: {
    marginHorizontal: 4,
    marginVertical: 2,
    paddingVertical: 8,
    paddingHorizontal: 12,
    borderRadius: 16,
    borderWidth: 1,
  },

  containerImages: {
    alignItems: 'center',
    marginTop: 16,
  },

  imageContainer: {
    width: '100%',
    height: 200,
    borderRadius: 8,
    overflow: 'hidden',
  },

  image: {
    width: '100%',
    height: '100%',
    resizeMode: 'cover',
  },

  errorText: {
    color: 'red',
    marginTop: 8,
  },
};

export { styles };
