import { StyleSheet } from 'react-native';

export const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  stepTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  labelContainer: {
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  // Add more styles as needed
  button_next: {
    backgroundColor: '#4CAF50',
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
    alignSelf: 'flex-end',
  },
  buttonText_Next: {
    color: 'white',
    fontWeight: 'bold',
  },
  button_prev: {
    backgroundColor: '#f44336',
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
    alignSelf: 'flex-start',
  },
  buttonText_Prev: {
    color: 'white',
    fontWeight: 'bold',
  },
  button_add: {
    backgroundColor: '#2196F3',
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
  },
  buttonText_Add: {
    color: 'white',
    fontWeight: 'bold',
  },
  button_cancel: {
    backgroundColor: '#777',
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
  },
  buttonText_Cancel: {
    color: 'white',
    fontWeight: 'bold',
  },
});
